    }
    nav{
        ul{
            display: flex;
            li{
                margin: 0 15px;
                a{
                    font-size: $font-14;
                    color: $white;
                }
            }
            i{
                color: $white;
            }
        }
    }
}
div.top{
        @include width;
        padding: 11px 0;
        background-color: $dark;
        left: 0; top: 0;
    p{
        @include width($wd:447px);
        a{
            font-size: $font-12;
